var newUsers = [];
var errors = false;

$('#new-users tbody tr').each(function(){
	var email = $(this).find('input[name="email"]').val()
	if (!_.isEmpty(email)){
		if (validateEmail(email)){

			// Build Teams Array
			teams = $(this).find('select[name="teams"]').val() || [];
			role = "Role::" + $(this).find('select[name="roles"]').val();
			teams.push(role);

			// Build New User Object
			newUser = {
				email: $(this).find('input[name="email"]').val(),
				displayName: $(this).find('input[name="email"]').val(),
				profileAttributes: [],
				teams: teams,
				spaceAdmin: $(this).find('input[name="spaceAdmin"]').is(':checked')
			}

			// Push new user to new users array
			newUsers.push(newUser);
		} else {
			$(this).first('td input span').text('Please enter a valid email!');
			$(this).addClass('has-error');
	        errors = true;
		}
	}
});

if (!errors && !_.isEmpty(newUsers)){
	K('field[Users JSON]').value(JSON.stringify(newUsers));
	K('form').submitPage();
} else if (_.isEmpty(newUsers)){
	$('#inviteUsersContainer div.message').notifie({ type: 'alert', message: K.translate('bundle','Enter some users to invite!')});
}

function validateEmail(email) {
  	var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
 	return re.test(email);
}

// Hide Submit button while table loads
K("button[Submit]").hide();
$("#inviteUsersContainer").hide();

// Global variable to store users table
var table = $("#new-users");
var teams;
var roles;
var currentUsers;
var newUsers;

// Build up variables to store teams/users from bridge
var deferredTeams = jQuery.Deferred();
var deferredRoles = jQuery.Deferred();
var deferredUsers = jQuery.Deferred();

// Get Teams from Bridge
K("bridgedResource[Teams]").load({
  attributes: ["Name"],
  success: function(bridgedData) {
    deferredTeams.resolve(
      bridgedData.map(function(item) {
        return item.Name;
      })
    );
  }
});

// Get Roles from Bridge
K("bridgedResource[Roles]").load({
  attributes: ["Name"],
  success: function(bridgedData) {
    deferredRoles.resolve(
      bridgedData.map(function(item) {
        return item.Name.split("::")[1];
      })
    );
  }
});
// Get Users from Bridge
K("bridgedResource[Users]").load({
  attributes: ["Email"],
  success: function(bridgedData) {
    deferredUsers.resolve(
      bridgedData.map(function(item) {
        return item.Email;
      })
    );
  }
});

// Wait for Bridge to finish executing
$.when(
  deferredTeams.promise(),
  deferredRoles.promise(),
  deferredUsers.promise()
).then(function(t, r, u) {
  // Store Users & teams in global variables
  teams = t;
  //roles = r;
  // Temporary workaround to only allow for specific roles to be added to the roles list.
  // TODO: add attribute to the Roles Teams that we can use to filter the list here or in the bridge call.
  roles = ["Contractor", "Employee", "Vendor"];
  users = u;

  // Build the table
  initializeTable();
});

// Initialize Users Table
function initializeTable(teams, roles) {
  // Add two initial rows to the table
  $(table)
    .find("tbody")
    .append(buildRow());
  $(table)
    .find("tbody")
    .append(buildRow());

  // Build a last row that will be used to add more rows
  lastRow = buildRow();

  // Build functionality to make the last row always add another row.
  $(lastRow).attr("id", "addARowRow");
  $(lastRow)
    .find("input")
    .click(function() {
      $(buildRow()).insertBefore($("#addARowRow"));
      $("#addARowRow")
        .prev()
        .find('input[name="email"]')
        .focus();
    });
  // Add the last row to the table
  $(table)
    .find("tbody")
    .append(lastRow);

  // Display the Table and Submit Button
  $("#inviteUsersContainer").fadeIn({ duration: 1000 });
  K("button[Submit]").show();

  // Populate link to User Management in the admin kapp
  $("h5 a.userManagementLink").attr(
    "href",
    "/" + bundle.spaceSlug() + "/" + bundle.config.adminKappSlug + "/users"
  );
}

function buildRow() {
  var newRow = $("<tr>").append(
    $("<td>").append(
      $("<div>", { class: "form-group" }).append(
        $("<input>", {
          type: "text",
          placeholder: "Email",
          name: "email",
          class: "form-control"
        }),
        $("<span>", { class: "help-block text-center" })
      )
    ),
    $("<td>").append(
      $("<select>", { name: "roles" }).append(buildOptions(roles))
    ),
    $("<td>").append(
      $("<select>", {
        multiple: "multiple",
        name: "teams"
      }).append(buildOptions(teams))
    ),
    $("<td>").append(
      $("<center>").append(
        $("<input>", { type: "checkbox", name: "spaceAdmin" })
      )
    ),
    $("<td>", { align: "center" }).append(
      $("<button>", {
        title: "Remove",
        class: "btn btn-xs btn-danger remove"
      }).append($("<span>", { class: "fa fa-times fa-fw" }))
    )
  );

  return newRow;
}

// Function used to build up options Array
function buildOptions(optionsArray) {
  var options = "";
  $.each(optionsArray, function() {
    // TODO: add a defualt attribute to Roles Teams then chage this conditional to pick up on that attribute.
    if (this == "Employee") {
      options += '<option value="' + this + '"  selected>' + this + "</option>";
    } else {
      options += '<option value="' + this + '">' + this + "</option>";
    }
  });
  return options;
}

function validateEmail(email) {
  var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(email);
}

// Add Row Click Handler
$("a#add_row").click(function() {
  addEmptyRow();
});

// Validate Entered Email Address (ensure user doesn't already exist)
$(table).delegate('tr td input[name="email"]', "blur", function(e) {
  enteredVal = $(this).val();
  if (!_.isEmpty(enteredVal) && users.includes(enteredVal)) {
    $(this)
      .next()
      .text("This user already exists!");
    $(this)
      .parent()
      .addClass("has-error");
    $(this).val(null);
    $(this).focus();
  } else if (!_.isEmpty(enteredVal) && !validateEmail(enteredVal)) {
    $(this)
      .next()
      .text("Please enter a valid email!");
    $(this)
      .parent()
      .addClass("has-error");
    $(this).focus();
  } else {
    $(this)
      .parent()
      .removeClass("has-error");
    $(this)
      .next()
      .text("");
  }
});

// Delete Row Click Handler
$(table).delegate("button.remove", "click", function(e) {
  var tr = $(this).closest("tr");
  if ($(tr).attr("id") !== "addARowRow") {
    $(this)
      .closest("tr")
      .remove();
  }
});

// Clone Row Click Handler
$(table).delegate("button.clone", "click", function(e) {
  // Clone the selected Row
  newRow = $(this)
    .closest("tr")
    .clone();
  // Get a list of the Teams that were previously selected
  var selectedTeams = $(this)
    .closest("tr")
    .find("select")
    .val();
  // Remove the bootstrap-select plugin
  $(newRow)
    .find(".bootstrap-select")
    .replaceWith(function() {
      return $("select", this);
    });
  // Re-create the select-picker plugin
  $(newRow)
    .find("td select.selectpicker")
    .selectpicker({ iconBase: "fa", tickIcon: "fa-check" });
  $(newRow)
    .find("td select.selectpicker")
    .selectpicker("val", selectedTeams);
  // Append the new row to the table
  $(newRow).insertBefore($("#addARowRow"));
  $("#addARowRow")
    .prev()
    .find('input[name="email"]')
    .focus();
});
